INSERT INTO `{TABLE_PREFIX}mod_wbs_core_templates_of_letter` (`letter_template_name`, `letter_template_body`, `letter_template_subject`, `letter_template_description`) VALUES
('success_registration', '    <p style=\'text-align:left;\'>         <h1>{{SURNAME}} {{NAME}},</h1>         Поздравляем Вас с успешной регистрацией на всероссийском портале Инфо-РФ.РФ!<br><br>         Пожалуйста, перейдите по ссылке <a href=\'{{WB_URL}}/modules/wbs_auth/api.php?action=confirm_email&code={{CONFIRM_EMAIL_CODE}}\'>{{WB_URL}}/modules/wbs_auth/api?action=confirm_email&code={{CONFIRM_EMAIL_CODE}}</a> для подтверждения email-адреса.     </p><br>      <p>     Регистрационные данные:<br>          Логин: {{USERNAME}}<br>     Email: {{EMAIL}}<br><br>     </p> {{ADDITIONAL_INFO}}         <p>После подтверждения email-адреса Вы сможете в красках рассказать о своей организации всему русскоязычному населению.</p>          <p>С уважением, команда Инфо-РФ.РФ.</p>', 'Регистрация на портале Инфо-РФ.РФ', 'Письмо модуля wbs_auth'),
('repair_password', '<p>Здравствуйте!</p><p>Пароль успешно восстановлен.</p><p><br></p><p>Ваш логин: <b>{{USERNAME}}</b></p><p>Новый пароль: <b>{{PASSWORD}}</b></p><p><br></p>', 'Восстановление пароля', 'Письмо модуля wbs_auth');
